#include <stdlib.h>

extern int z;
int z=0;
void blackhole(char *arr) {
    int acc=arr[6];
    for(int j=0; j < 50000; j++) {
        acc+=arr[j];
    }
    z += acc;
    free(arr);
}

int main(void) {
    for(int i=0; i < 10000; i++) {
        char *arr = malloc(1000000);
        for(int j=0; j < 100000; j++) {
            arr[j] = i^j;
        }
        blackhole(arr);
    }
}
